import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Carousel from 'react-bootstrap/Carousel';

import { API_KEY } from '../utils/constants';

const MovieCarousel = () => {
  const [movies, setMovies] = useState([]);
  const [selectedMovie, setSelectedMovie] = useState(null);

  useEffect(() => {
    axios.get(`https://api.themoviedb.org/3/discover/movie?api_key=${API_KEY}&language=en-US&sort_by=popularity.desc&include_adult=false&include_video=false&page=1`)
      .then(res => {
        setMovies(res.data.results);
      })
      .catch(err => {
        console.log(err);
      });
  }, []);

  const handleClick = (movie) => {
    setSelectedMovie(movie);
  }

  return (
    <div>
      <Carousel>
        {movies.map(movie => (
          <Carousel.Item key={movie.id}>
            <img
              className="video d-block w-100"
              src={`https://image.tmdb.org/t/p/w780/${movie.poster_path}`}
              alt={movie.title}
              onClick={() => handleClick(movie)}
            />
            <Carousel.Caption>
              <h3>{movie.title}</h3>
            </Carousel.Caption>
          </Carousel.Item>
        ))}
      </Carousel>
      {selectedMovie && (
        <div>
          <h2>{selectedMovie.title}</h2>
          <video src={`https://www.youtube.com/watch?v=${selectedMovie.videos.results[0].key}`} controls />
        </div>
      )}
    </div>
  );
}

export default MovieCarousel;
