import React, { useEffect, useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { fetchMovies, getGenres } from "../store";
import Slider from "../components/slider";

const Trending = () => {

    const [isScrolled, setIsScrolled] = useState(false);
  const movies = useSelector((state) => state.cinera.movies);
  const genres = useSelector((state) => state.cinera.genres);
  const genresLoaded = useSelector((state) => state.cinera.genresLoaded);

  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(getGenres());
  }, []);

  useEffect(() => {
    if (genresLoaded) {
      dispatch(fetchMovies({ genres, type: "all" }));
    }
  }, [genresLoaded]);

  window.onscroll = () => {
    setIsScrolled(window.pageYOffset === 0 ? false : true);
    return () => (window.onscroll = null);
  };

    return(

        // Top Trending Content 
        <div className="trending-content-div p-0 g-0 m-0">

            <Slider movies={movies} />

            <div className="trending-heading ms-4 fw-bolder fs-1">
                    TOP TRENDING
                </div>

            <div className="container">

                <div className="row trending-row">
                    <div className="col-12 col-lg-6 d-flex justify-content-center">
                        hi
                        </div>
                    <div className="col-12 col-lg-6 d-flex justify-content-center">
                        bi
                        </div>
                </div>
            </div>

        </div>
    );
}

export default Trending;
