import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Carousel from 'react-bootstrap/Carousel';

import { API_KEY } from '../utils/constants';

const MovieCarousel = () => {
  const [movies, setMovies] = useState([]);
  const [selectedMovie, setSelectedMovie] = useState(null);
  const [activeIndex, setActiveIndex] = useState(0);
  const [youtubeVideoKey, setYoutubeVideoKey] = useState(null);
  const [isLoading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);

  useEffect(() => {
    setLoading(true);
    setError(null);
    axios.get(`https://api.themoviedb.org/3/trending/movie/week?api_key=${API_KEY}&language=en-US&sort_by=popularity.desc&include_adult=false&include_video=false&page=${page}`)
      .then(res => {
        setMovies(prevMovies => [...prevMovies, ...res.data.results]);
        setTotalPages(res.data.total_pages);
        setLoading(false);
      })
      .catch(err => {
        console.log(err);
        setLoading(false);
        setError(err);
      });
  }, [page]);

  useEffect(() => {
    if (selectedMovie) {
      setYoutubeVideoKey(null);
      axios.get(`https://api.themoviedb.org/3/movie/${selectedMovie.id}/videos?api_key=${API_KEY}&language=en-US`)
        .then(res => {
          const youtubeVideos = res.data.results.filter(video => video.site === 'YouTube');
          setYoutubeVideoKey(youtubeVideos[0].key);
        })
        .catch(err => {
          console.log(err);
        });
    }
  }, [selectedMovie]);

  const handleClick = (movie) => {
    setSelectedMovie(movie);
  }

  const handleSelect = (selectedIndex) => {
    setActiveIndex(selectedIndex);
  }

  const handleLoadMore = () => {
    setPage(prevPage => prevPage + 1);
  }

  if (isLoading) {
    return <p>Loading...</p>
  }

  if (error) {
    return <p>Error: {error.message}</p>
  }

  return (
    <div>
      <Carousel activeIndex={activeIndex} onSelect={handleSelect} indicators={false} interval={6000}>
        {movies.map(movie => (
          <Carousel.Item key={movie.id}>
            <img
                            className="d-block w-100"
                            src={`https://image.tmdb.org/t/p/original/${movie.backdrop_path}`}
                            alt={movie.title}
                            onClick={() => handleClick(movie)}
                          />
                          </Carousel.Item>
                      ))}
                    </Carousel>

                    <div className="d-flex mt-3 mb-3 justify-content-center">
                        {movies.map((_, index) => (
                          <div
                            key={index}
                            className={`d-flex justify-content-center align-items-center rounded-circle m-2 ${
                              index === activeIndex ? "text-white bg-secondary" : "bg-dark"
                            }`}
                            style={{ width: "12px", height: "12px" }}
                            onClick={() => handleSelect(index)}
                          ></div>
                        ))}
                      </div>
                      {selectedMovie && movies[activeIndex] === selectedMovie && (
                      <div className='m-4'>
                        <h2 className='text-light'>{selectedMovie.title}</h2>
                        {youtubeVideoKey &&  (
                          <a href={`https://www.youtube.com/watch?v=${youtubeVideoKey}`} target="_blank" rel="noopener noreferrer">
                            Watch Trailer
                          </a>
                        )}
                      </div>
                    )}
                    {page < totalPages && (
                      <button onClick={handleLoadMore}>Load More</button>
                    )}
                  </div>
                );
              }

              export default MovieCarousel;
