import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Carousel from 'react-bootstrap/Carousel';

const MovieCarousel = () => {
  const [movies, setMovies] = useState([]);
  const [activeIndex, setActiveIndex] = useState(0);
  const [isPaused, setIsPaused] = useState(false);

  useEffect(() => {
    axios.get('https://api.themoviedb.org/3/trending/movie/week?api_key=YOUR_API_KEY&language=en-US&sort_by=popularity.desc&include_adult=false&include_video=false&page=1')
      .then(res => {
        setMovies(res.data.results);
      })
      .catch(err => {
        console.log(err);
      });
  }, []);

  const handleSelect = (selectedIndex) => {
    setActiveIndex(selectedIndex);
  }

  const handleMouseEnter = () => {
    setIsPaused(true);
  }

  const handleMouseLeave = () => {
    setIsPaused(false);
  }

  return (
    <div onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave}>
      <Carousel activeIndex={activeIndex} onSelect={handleSelect} indicators={false} interval={null} pause={isPaused}>
        {movies.map((movie) => (
          <Carousel.Item key={movie.id}>
            <img
              className="d-block w-100"
              src={`https://image.tmdb.org/t/p/w1280/${movie.backdrop_path}`}
              alt={movie.title}
            />
          </Carousel.Item>
        ))}
      </Carousel>
      <div className="movie-details">
        <h3 className="title">{movies[activeIndex].title}</h3>
        <p className="overview">{movies[activeIndex].overview}</p>
        </div>
    </div>
  );
}

export default MovieCarousel;
