import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { fetchMovies, getGenres } from "../store";
import Slider from "../components/slider";

const Trending = ({movieData, isliked = false}) => {


  const movies = useSelector((state) => state.cinera.movies);
  const genres = useSelector((state) => state.cinera.genres);
  const genresLoaded = useSelector((state) => state.cinera.genresLoaded);

  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(getGenres());
  }, []);

  useEffect(() => {
    if (genresLoaded) {
      dispatch(fetchMovies({ genres, type: "all" }));
    }
  }, [genresLoaded]);

    return(

        // Top Trending Content 
        <div className="trending-content-div p-0 g-0 m-0">

            <div className="trending-heading ms-5 fw-bolder fs-1">
                    TOP TRENDING
                </div>

                <Slider movies={movies}/>

            <div className="container">

            <div id="carouselExampleSlidesOnly" className="carousel slide" data-ride="carousel">
  <div className="carousel-inner">
    <div className="carousel-item active">
    <img src = {`https://image.tmdb.org/t/p/original${movieData.image}`} alt="movies"/>
    </div>
    <div className="carousel-item">
      <img className="d-block w-100" src="..." alt="Second slide"/>
    </div>
    <div className="carousel-item">
      <img className="d-block w-100" src="..." alt="Third slide"/>
    </div>
  </div>
</div>

            </div>

        </div>
    );
}

export default Trending;
